        generation_id = item.get('generation_id') or metadata.get('generation_id') or metadata.get('task_id')
        if generation_id:
            item['generation_id'] = str(generation_id)

        def _looks_like_remote(path_text: str) -> bool:
            prefix = path_text.lower()
            return prefix.startswith('http://') or prefix.startswith('https://')

        resolved_path = item.get('resolved_path')
        candidate_paths = [
            resolved_path,
            item.get('absolute_path'),
            metadata.get('local_path'),
            item.get('file_path'),
        ]

        abs_path: Optional[str] = None
        for candidate in candidate_paths:
            if not candidate:
                continue
            text = str(candidate)
            if _looks_like_remote(text):
                abs_path = text
                break
            candidate_path = Path(text)
            abs_candidate = candidate_path if candidate_path.is_absolute() else (root_path / candidate_path)
            abs_path = str(abs_candidate.resolve())
            break

        if not abs_path:
            rel = item.get('relative_path') or metadata.get('relative_path')
            if rel:
                rel_text = str(rel)
                if _looks_like_remote(rel_text):
                    abs_path = rel_text
                else:
                    rel_path = Path(rel_text)
                    abs_path = str((root_path / rel_path).resolve())

        if abs_path:
            item['absolute_path'] = abs_path
            item['local_path'] = abs_path
            item['file_path'] = abs_path

        # 记录相对路径
