interface TaskDetailModalProps {
  task: TaskRecord | null
  open: boolean
  onClose: () => void
  onNavigate?: (direction: 'prev' | 'next') => void
  onExportSuccess?: (snapshot: ExportSnapshot) => void
}

const extractFileMeta = (task: TaskRecord | null) => {
  if (!task) {
    return {
      localPath: null,
      remoteUrl: null,
      mime: null,
      fileId: null
    }
  }
  const primaryOutput = (task.envelope?.primary_output ?? {}) as Record<string, unknown>
  const localPath =
    typeof primaryOutput.local_path === 'string' && primaryOutput.local_path
      ? (primaryOutput.local_path as string)
      : task.outputPath ?? null
  const remoteUrl =
    typeof primaryOutput.url === 'string' && primaryOutput.url
      ? (primaryOutput.url as string)
      : null
  const mime = typeof primaryOutput.mime === 'string' ? (primaryOutput.mime as string) : null
  const fileId =
    typeof primaryOutput.file_id === 'string' && primaryOutput.file_id
      ? (primaryOutput.file_id as string)
      : task.fileId ?? null

  return { localPath, remoteUrl, mime, fileId }
}

const getFallbackDescription = (task: TaskRecord | null) => {
  if (!task) return null
  const fallback = task.envelope?.fallback
  if (fallback && typeof fallback === 'object' && (fallback as { applied?: boolean }).applied) {
    const reasons = Array.isArray((fallback as { reasons?: string[] }).reasons)
      ? (fallback as { reasons?: string[] }).reasons
      : []
    const summary = typeof (fallback as { summary?: string }).summary === 'string'
      ? (fallback as { summary?: string }).summary
      : null
    const merged = [summary, ...reasons].filter(Boolean)
    return merged.length > 0 ? merged.join('；') : '已触发回退策略，请参考日志。'
  }
  return null
}

const formatBytes = (size?: number | null) => {
  if (!size || size <= 0) return null
  if (size >= 1024 * 1024) {
    return `${(size / (1024 * 1024)).toFixed(1)} MB`
  }
  if (size >= 1024) {
    return `${(size / 1024).toFixed(0)} KB`
  }
  return `${size} B`
}

const toNumber = (value: unknown): number | undefined => {
  if (typeof value === 'number' && Number.isFinite(value)) {
    return value
  }
  if (typeof value === 'string') {
    const trimmed = value.trim()
    if (!trimmed) return undefined
    const numeric = Number(trimmed)
    if (Number.isFinite(numeric)) {
      return numeric
    }
  }
  return undefined
}

export const TaskDetailModal = ({ task, open, onClose, onNavigate, onExportSuccess }: TaskDetailModalProps) => {
  const tailId = task ? task.id.slice(-8) : ''
  const updatedLabel = task?.updatedAt
    ? new Date(task.updatedAt).toLocaleString()
    : task?.createdAt
      ? new Date(task.createdAt).toLocaleString()
      : '—'
  const { localPath, remoteUrl, mime, fileId } = extractFileMeta(task)
  const { message } = AntdApp.useApp()
  const fallbackDescription = getFallbackDescription(task)
  const storageLocal = (task?.envelope?.storage_local ?? {}) as {
    primary?: Record<string, unknown>
    root?: string
  }
  const storagePrimary = (storageLocal.primary ?? {}) as Record<string, unknown>
  const primaryOutput = (task?.envelope?.primary_output ?? {}) as Record<string, unknown>
  const thumbnailSource =
    (typeof task?.thumbnailPath === 'string' && task.thumbnailPath) ||
    (typeof primaryOutput.thumbnail_path === 'string' ? (primaryOutput.thumbnail_path as string) : undefined) ||
    (typeof storagePrimary.thumbnail === 'string' ? (storagePrimary.thumbnail as string) : undefined) ||
