  const isVideoTask = Boolean(task?.model?.startsWith('video'))
  const inputParams = (task?.input ?? {}) as Record<string, unknown>
  const envelopeParams = (task?.envelope?.parameters ?? {}) as Record<string, unknown>
  const storageRoot = typeof storageLocal.root === 'string' ? storageLocal.root : undefined
  const readParam = (key: string): unknown => {
    if (Object.prototype.hasOwnProperty.call(inputParams, key) && inputParams[key] !== undefined) {
