  }
  const renderStatusTag = (status?: string) => {
    const normalized = (status ?? 'UNKNOWN').toUpperCase()
    const colorMap: Record<string, string> = {
      SUCCEEDED: 'green',
      SUCCESS: 'green',
      FAILED: 'red',
      RUNNING: 'blue',
      PENDING: 'default',
      CANCELLED: 'orange'
    }
    const color = colorMap[normalized] ?? 'default'
    return <Tag color={color}>{normalized}</Tag>
  }
  const widthValue = toNumber(storagePrimary.width ?? envelopeParams.width ?? inputParams.width)
  const heightValue = toNumber(storagePrimary.height ?? envelopeParams.height ?? inputParams.height)
  const widthHeightLabel = widthValue && heightValue ? `${widthValue} × ${heightValue}` : '—'
  const resolutionGrade = readParam('resolution_grade') as string | undefined
  const ratioValue = readParam('ratio') as string | undefined
  const seedValue = readParam('seed')
  const guidanceScale = readParam('guidance_scale')
  const motionStrength = readParam('motion_strength')
  const watermarkValue = readParam('watermark')
  const cameraFixedValue = readParam('camera_fixed')
  const useProModelValue = readParam('use_pro_model')
  const autoCompressValue = readParam('auto_compress_references')
  const referenceModeValue = readParam('reference_mode')
  const referenceRawAssets = Array.isArray(inputParams.referenceImages)
    ? (inputParams.referenceImages as unknown[])
    : []

  type ReferenceEntry = {
    id: string
    label: string
    copyText?: string
    localPath?: string
    relativePath?: string
    remoteUrl?: string
    dataUrl?: string
