    width?: number
    height?: number
  }

  const normalizeReferenceEntries = (
    assets: unknown[],
    root: string | undefined,
    taskId: string | undefined
  ): ReferenceEntry[] => {
    const trim = (value: string) => value.trim()
    const isRemote = (value: string) => /^https?:/i.test(value)
    const isDataUrl = (value: string) => /^data:/i.test(value)
    const joinPath = (relative?: string | null) => {
      if (!relative || !root) return relative ?? undefined
      const base = root.replace(/\\/g, '/').replace(/\/+$/, '')
      const rel = relative.replace(/\\/g, '/').replace(/^\/+/, '')
