      if (!value) return false
      if (value.startsWith('/')) return true
      return /^[a-zA-Z]:[\\/]/.test(value)
    }

    return assets.map((item, index) => {
      const entryId = `${taskId ?? 'history'}-ref-${index}`
      let label = `参考${index + 1}`
      let copyText: string | undefined
      let localPath: string | undefined
      let remoteUrl: string | undefined
      let dataUrl: string | undefined
      let relativePath: string | undefined
      let size: number | undefined
      let width: number | undefined
      let height: number | undefined

      if (item && typeof item === 'object') {
        const obj = item as Record<string, unknown>
        const candidateLabel =
          obj.label ?? obj.name ?? obj.source ?? obj.filename ?? obj.fileName ?? obj.file_path
        if (typeof candidateLabel === 'string' && candidateLabel.trim()) {
          label = candidateLabel.trim()
        }

        const pathCandidates = [
          obj.absolute_path,
          obj.local_path,
          obj.path,
          obj.relative_path,
          obj.file_path,
          obj.filePath,
          obj.url
        ]

        for (const candidate of pathCandidates) {
          if (typeof candidate !== 'string' || !candidate.trim()) continue
          const value = trim(candidate)
          if (isDataUrl(value)) {
            dataUrl = value
            copyText = copyText ?? value
            break
          }
          if (isRemote(value)) {
            remoteUrl = value
            copyText = copyText ?? value
            continue
          }
          if (isAbsolutePath(value)) {
            localPath = value
            copyText = copyText ?? value
            break
          }
          relativePath = value
          const joined = joinPath(value)
          if (joined) {
            localPath = joined
            copyText = copyText ?? joined
          }
        }

        if (!copyText && typeof obj.id === 'string') {
          copyText = obj.id
        }

        if (typeof obj.size === 'number') size = obj.size
        if (typeof obj.width === 'number') width = obj.width
        if (typeof obj.height === 'number') height = obj.height
      } else if (typeof item === 'string' && item.trim()) {
        const value = trim(item)
        if (isDataUrl(value)) {
          dataUrl = value
          copyText = value
        } else if (isRemote(value)) {
          remoteUrl = value
          copyText = value
        } else if (isAbsolutePath(value)) {
          localPath = value
          copyText = value
        } else {
          relativePath = value
          const joined = joinPath(value)
          if (joined) {
            localPath = joined
            copyText = joined
          } else {
            copyText = value
          }
        }
      }

      if (!copyText) {
        copyText = localPath ?? remoteUrl ?? relativePath ?? label
      }

      return {
        id: entryId,
        label,
        copyText,
        localPath,
        relativePath,
        remoteUrl,
        dataUrl,
        size,
        width,
        height
      }
