  )

  const referenceCount = referenceEntries.length
  const thumbnailStrategy = task?.thumbnailStrategy ?? (storagePrimary.thumbnail_strategy as string | undefined)
  const relativePath = task?.relativePath ?? (storagePrimary.relative_path as string | undefined)
  const referenceSummaryLabel = referenceCount ? `${referenceCount} 项（见左侧列表）` : '—'
  const thumbnailStrategyLabel = formatOptionalValue(thumbnailStrategy)
  const thumbnailPathNode = thumbnailSource ? (
    <Text copyable={{ text: thumbnailSource }}>{thumbnailSource}</Text>
  ) : (
    '—'
  )
  const outputPathNode = task?.outputPath ? (
    <Text copyable={{ text: task.outputPath }}>{task.outputPath}</Text>
  ) : (
    '—'
  )
  const relativePathNode = relativePath ? (
    <Text copyable={{ text: relativePath }}>{relativePath}</Text>
  ) : (
    '—'
  )
  const storageRootNode = storageRoot ? (
    <Text copyable={{ text: storageRoot }}>{storageRoot}</Text>
  ) : (
    '—'
  )
  const fileIdNode = fileId ? (
    <Text copyable={{ text: fileId }}>{fileId}</Text>
  ) : (
    '—'
  )
  const seedLabel = formatSeedValue(seedValue)
  const ratioLabel = formatOptionalValue(ratioValue)
  const resolutionLabel = formatOptionalValue(resolutionGrade)
  const guidanceScaleLabel = formatOptionalValue(guidanceScale)
  const motionStrengthLabel = formatOptionalValue(motionStrength)
  const watermarkLabel = formatBooleanValue(watermarkValue)
  const cameraFixedLabel = formatBooleanValue(cameraFixedValue)
  const useProModelLabel = formatBooleanValue(useProModelValue)
  const autoCompressLabel = formatBooleanValue(autoCompressValue)
  const referenceModeLabel = formatOptionalValue(referenceModeValue)
  const durationLabel = typeof durationValue === 'number' && durationValue > 0 ? `${durationValue}s` : '—'
  const fpsLabel = typeof fpsValue === 'number' && fpsValue > 0 ? `${fpsValue} fps` : '—'
  const fileSizeDisplay = fileSizeLabel ?? '—'
  const statusDisplay = renderStatusTag(task?.status)
  const promptDisplay = promptText ? (
    <Paragraph
      copyable={{ text: promptText }}
      ellipsis={{ rows: 3, expandable: true, symbol: '展开' }}
      style={{ marginBottom: 0 }}
    >
      {promptText}
    </Paragraph>
  ) : (
    '—'
  )

  const [outputPreview, setOutputPreview] = useState<string | null>(null)
  const [previewSrc, setPreviewSrc] = useState<string | null>(null)
  const [referencePreviewMap, setReferencePreviewMap] = useState<Record<string, string | null>>({})

  useEffect(() => {
    let cancelled = false
    if (!open || !thumbnailSource) {
