const { Text, Title } = Typography

const STATUS_COLORS: Record<TaskStatus, string> = {
  PENDING: 'default',
  RUNNING: 'processing',
  SUCCEEDED: 'success',
  FAILED: 'error',
  CANCELLED: 'warning'
}

interface TaskSummaryCardProps {
  task: TaskRecord
  onExportSuccess?: (taskId: string, snapshot: ExportSnapshot) => void
}

export const TaskSummaryCard = ({ task, onExportSuccess }: TaskSummaryCardProps) => {
  const modelLabel = task.modelLabel ?? task.model
  const statusColor = STATUS_COLORS[task.status] ?? 'default'
  const tailId = useMemo(() => task.id.slice(-8), [task.id])

  const primaryOutput = (task.envelope?.primary_output ?? {}) as Record<string, unknown>

  const localPath =
    typeof primaryOutput.local_path === 'string' && primaryOutput.local_path
      ? primaryOutput.local_path
      : task.outputPath ?? null
  const remoteUrl =
    typeof primaryOutput.url === 'string' && primaryOutput.url ? primaryOutput.url : null
  const mime = typeof primaryOutput.mime === 'string' ? primaryOutput.mime : null
  const fileId =
    typeof primaryOutput.file_id === 'string' && primaryOutput.file_id
      ? primaryOutput.file_id
      : task.fileId ?? null

  const isVideoModel = task.model.startsWith('video')
  const hasVideoSource = isVideoModel && (localPath || remoteUrl)
  const showMissingVideo = isVideoModel && task.status === 'SUCCEEDED' && !hasVideoSource

  const schemaVersion = task.schemaVersion ? `Schema v${task.schemaVersion}` : null

  const handleExportSuccess = (snapshot: ExportSnapshot) => {
    onExportSuccess?.(task.id, snapshot)
  }

  const fallbackDescription = useMemo(() => {
    const fallback = task.envelope?.fallback
    if (fallback && typeof fallback === 'object' && (fallback as { applied?: boolean }).applied) {
      const reasons = Array.isArray((fallback as { reasons?: string[] }).reasons)
        ? (fallback as { reasons?: string[] }).reasons
        : []
      const summary = typeof (fallback as { summary?: string }).summary === 'string'
        ? (fallback as { summary?: string }).summary
        : null
      const merged = [summary, ...reasons].filter(Boolean)
      return merged.length > 0 ? merged.join('；') : '已触发回退策略，请参考日志。'
    }
    return null
  }, [task.envelope?.fallback])

  return (
    <Card
      size="small"
      bordered
      key={task.id}
      title={
        <Space align="center" size={8} wrap>
          <Title level={5} style={{ margin: 0 }}>
            {modelLabel}
          </Title>
          <Tag color={statusColor}>{task.status}</Tag>
          <Text type="secondary">#{tailId}</Text>
          {schemaVersion && <Tag color="purple">{schemaVersion}</Tag>}
        </Space>
      }
      extra={task.updatedAt ? <Text type="secondary">更新：{formatTime(task.updatedAt)}</Text> : undefined}
    >
      <Space direction="vertical" size="middle" style={{ width: '100%' }}>
        {task.error && <Alert message={task.error} type="error" showIcon />}
        {task.envelope?.message && !task.error && (
          <Alert message={task.envelope.message} type="info" showIcon icon={<InfoCircleOutlined />} />
        )}
        <Space align="start" size={24} wrap style={{ width: '100%' }}>
          <ThumbnailPreview
            thumbnailPath={task.thumbnailPath}
            taskId={task.id}
            modelLabel={modelLabel}
          />
          <Space direction="vertical" size="small" style={{ flex: 1, minWidth: 280 }}>
            <Space direction="vertical" size={4} style={{ width: '100%' }}>
              {localPath && (
                <Text type="secondary">
                  输出路径：{localPath}
                </Text>
              )}
              {remoteUrl && (
                <Text type="secondary">
                  远程地址：{remoteUrl}
                </Text>
              )}
              {task.thumbnailPath && (
                <Text type="secondary">
                  缩略图：{task.thumbnailPath}
                </Text>
              )}
              {task.relativePath && (
                <Text type="secondary">
                  相对路径：{task.relativePath}
                </Text>
              )}
            </Space>

            {hasVideoSource && (
              <VideoPlayer localPath={localPath} remoteUrl={remoteUrl} mime={mime} taskId={task.id} />
            )}

            {showMissingVideo && (
              <Alert
                type="warning"
                showIcon
                message="任务成功但未返回视频源，请检查后台日志或重新生成。"
                icon={<WarningOutlined />}
              />
            )}

            <TaskExportPanel
              model={task.model}
              fileId={fileId}
              snapshot={task.exportResult ?? null}
              onExportSuccess={handleExportSuccess}
            />

            {fallbackDescription && (
              <Alert
                type="warning"
                showIcon
                message="已启用回退策略"
                description={fallbackDescription}
              />
            )}

            <Text type="secondary">
              日志记录：请查看 <Text code>docs/verification_logs/</Text> 获取任务 {tailId} 的导出与回退详情。
            </Text>
          </Space>
        </Space>
      </Space>
    </Card>
  )
}

