{"output":"import { useCallback, useEffect, useMemo, useState } from 'react'
import { Empty } from 'antd'

import type { TaskRecord, ExportSnapshot } from '../../store/taskOrchestrator'
import { useHotkeys } from '../../hooks/useHotkeys'
import { TaskHistoryList } from './task-history-list'
import { TaskDetailModal } from './task-detail-modal'
import { GenerationGallery } from '../history/GenerationGallery'

interface TaskHistoryPanelProps {
  tasks: TaskRecord[]
  onExportSuccess?: (taskId: string, snapshot: ExportSnapshot) => void
  displayMode?: 'list' | 'gallery'
}

export const TaskHistoryPanel = ({ tasks, onExportSuccess, displayMode = 'list' }: TaskHistoryPanelProps) => {
  const [activeId, setActiveId] = useState<string | null>(null)
  const [detailOpen, setDetailOpen] = useState(false)

  useEffect(() => {
    if (!activeId && tasks.length > 0) {
      setActiveId(tasks[0].id)
    }
    if (activeId && tasks.every((task) => task.id !== activeId)) {
      setActiveId(tasks[0]?.id ?? null)
      setDetailOpen(false)
    }
  }, [tasks, activeId])

  const activeIndex = useMemo(
    () => (activeId ? tasks.findIndex((task) => task.id === activeId) : -1),
    [tasks, activeId]
  )
  const activeTask = activeIndex >= 0 ? tasks[activeIndex] : null
  const isHistoryTask = activeTask?.id.startsWith('history:') ?? false

  const selectByIndex = useCallback((index: number) => {
    if (index < 0 || index >= tasks.length) return
    setActiveId(tasks[index].id)
  }, [tasks])

  const handleNavigate = useCallback((direction: 'prev' | 'next') => {
    if (tasks.length === 0) return
    if (activeIndex < 0) {
      selectByIndex(0)
      return
    }
    const nextIndex = direction === 'prev' ? activeIndex - 1 : activeIndex + 1
    if (nextIndex >= 0 && nextIndex < tasks.length) {
      selectByIndex(nextIndex)
    }
  }, [tasks, activeIndex, selectByIndex])

  const hotkeys = useMemo(
    () => [
      {
        key: 'ArrowUp',
        handler: () => handleNavigate('prev'),
        preventDefault: true
      },
      {
        key: 'ArrowDown',
        handler: () => handleNavigate('next'),
        preventDefault: true
      },
      {
        key: 'Enter',
        handler: () => {
          if (activeTask) {
            setDetailOpen(true)
          }
        },
        preventDefault: true
      },
      {
        key: 'Escape',
        handler: () => setDetailOpen(false),
        preventDefault: true
      }
    ],
    [activeTask, handleNavigate]
  )

  useHotkeys(hotkeys, detailOpen)

  if (tasks.length === 0) {
    return <Empty description="尚无任务记录" />
  }

  return (
    <>
      {displayMode === 'gallery' ? (
        <GenerationGallery
          tasks={tasks}
          activeId={activeId}
          onSelect={(id) => setActiveId(id)}
          onOpenDetail={(id) => {
            setActiveId(id)
            setDetailOpen(true)
          }}
        />
      ) : (
        <TaskHistoryList
          tasks={tasks}
          activeId={activeId}
          onSelect={(id) => setActiveId(id)}
          onOpenDetail={(id) => {
            setActiveId(id)
            setDetailOpen(true)
          }}
        />
      )}
      <TaskDetailModal
        task={detailOpen ? activeTask : null}
        open={detailOpen}
        onClose={() => setDetailOpen(false)}
        onNavigate={(direction) => {
          handleNavigate(direction)
        }}
        onExportSuccess={
          detailOpen && activeTask && onExportSuccess && !isHistoryTask
            ? (snapshot) => onExportSuccess(activeTask.id, snapshot)
            : undefined
        }
      />
    </>
  )
}

export default TaskHistoryPanel
