{"output":"import { useEffect, useState } from 'react'

import type { ExportBundleResult } from '@shared/ipc-types'
import type { ModelKey } from '../../constants/schema'
import type { ExportSnapshot } from '../../store/taskOrchestrator'

import { Alert, Button, Space, Typography, App as AntdApp } from 'antd'

const { Text } = Typography

const resolveRecordType = (model: ModelKey): 'image' | 'video' => {
  if (model.startsWith('video')) {
    return 'video'
  }
  return 'image'
}

interface TaskExportPanelProps {
  model: ModelKey
  fileId?: string | null
  snapshot?: ExportSnapshot | null
  onExportSuccess?: (snapshot: ExportSnapshot) => void
}

const formatPath = (value?: string | null) => {
  if (!value) return '-'
  return value
}

const toSnapshot = (result: ExportBundleResult): ExportSnapshot => {
  const recordSummaries = Array.isArray(result.records)
    ? result.records.map((item) => {
        const fileId = typeof item.file_id === 'string' ? item.file_id : String(item.file_id ?? '')
        const filename = typeof item.filename === 'string' ? item.filename : undefined
        const recordType = String(item.record_type ?? '').toLowerCase() === 'video' ? 'video' : 'image'
        const referenceAssets = Array.isArray(item.reference_assets)
          ? (item.reference_assets as unknown[])
          : []
        return {
          fileId,
          filename,
          recordType,
          referenceCount: referenceAssets.length
        }
      })
    : []

  return {
    zipPath: result.zipPath ?? null,
    destinationZip: result.destinationZip ?? null,
    manifestPath: result.destinationManifestJson ?? result.manifestJson ?? null,
    referenceCount: result.referenceCount ?? null,
    missingRecords: result.missingRecords?.length ?? null,
    missingFiles: result.missingMainFiles?.length ?? null,
    missingReferences: result.missingReferences?.length ?? null,
    executedAt: Date.now(),
    records: recordSummaries,
    status: result.status ?? null,
    opId: result.opId ?? null,
    storageRoot: result.storageRoot ?? null
  }
}

const formatTimestamp = (value?: number | null) => {
  if (!value) return '-'
  try {
    return new Date(value).toLocaleString()
  } catch {
    return '-'
  }
}

const TaskExportPanel = ({ model, fileId, snapshot, onExportSuccess }: TaskExportPanelProps) => {
  const { message } = AntdApp.useApp()
  const [loading, setLoading] = useState(false)
  const [lastSnapshot, setLastSnapshot] = useState<ExportSnapshot | null>(snapshot ?? null)

  useEffect(() => {
    setLastSnapshot(snapshot ?? null)
  }, [snapshot])

  const recordType = resolveRecordType(model)
  const disabled = !fileId || loading

  const handleExport = async (withDestination: boolean) => {
    if (!fileId) return
    setLoading(true)
    try {
      let destination: string | undefined
      if (withDestination) {
        const chooseResponse = await window.api.exports.chooseDestination()
        if (!chooseResponse.success) {
          message.error(chooseResponse.error.message)
          return
        }
        if (chooseResponse.data.canceled || !chooseResponse.data.path) {
          return
        }
        destination = chooseResponse.data.path ?? undefined
      }

      const response = await window.api.exports.bundle({
        fileId,
        recordType,
        destination
      })

      if (!response.success) {
        const { code, message: errMsg } = response.error
        message.error(`导出失败（${code}）：${errMsg}，请查看日志。`)
        return
      }

      const result = response.data
      const nextSnapshot = toSnapshot(result)
      setLastSnapshot(nextSnapshot)
      onExportSuccess?.(nextSnapshot)
      const exportedCount = Array.isArray(result.records) ? result.records.length : 0
      const zipPath = result.destinationZip ?? result.zipPath
      const successLabel = exportedCount > 0 ? `导出成功（${exportedCount} 条记录）` : '导出成功'
      message.success(zipPath ? `${successLabel}：${zipPath}` : successLabel)
    } catch (error) {
      const msg = error instanceof Error ? error.message : String(error)
      message.error(`导出失败：${msg}，请查看日志。`)
    } finally {
      setLoading(false)
    }
  }

  return (
    <Space direction="vertical" size={4} style={{ width: '100%', marginTop: 8 }}>
      <Text type="secondary">file_id：{fileId ?? '任务结果尚未落盘'}</Text>
      <Space size="small">
        <Button
          type="primary"
          size="small"
          disabled={disabled}
          loading={loading}
          onClick={() => handleExport(false)}
        >
          导出记录
        </Button>
        <Button size="small" disabled={disabled} loading={loading} onClick={() => handleExport(true)}>
          导出到目录…
        </Button>
      </Space>
      {lastSnapshot && (
        <Space direction="vertical" size={2} style={{ width: '100%' }}>
          <Text type="secondary">
            最近导出包：{formatPath(lastSnapshot.destinationZip ?? lastSnapshot.zipPath)}
          </Text>
          {lastSnapshot.manifestPath && (
            <Text type="secondary">
              Manifest：{formatPath(lastSnapshot.manifestPath)}
            </Text>
          )}
          <Text type="secondary">执行时间：{formatTimestamp(lastSnapshot.executedAt)}</Text>
          <Text type="secondary">
            状态：{lastSnapshot.status ?? 'success'}
            {lastSnapshot.opId ? ` · 操作 ${lastSnapshot.opId}` : ''}
          </Text>
          {lastSnapshot.storageRoot && (
            <Text type="secondary">存储根目录：{lastSnapshot.storageRoot}</Text>
          )}
          {lastSnapshot.records && lastSnapshot.records.length > 0 && (
            <Space direction="vertical" size={2} style={{ width: '100%' }}>
              <Text type="secondary">包含记录：</Text>
              {lastSnapshot.records.slice(0, 4).map((item) => (
                <Text key={item.fileId} type="secondary">
                  {(item.recordType === 'video' ? '视频' : '图片')}
                  {item.fileId ? ` · ${item.fileId}` : ''}
                  {item.filename ? ` · ${item.filename}` : ''}
                  {typeof item.referenceCount === 'number' ? ` · 参考 ${item.referenceCount}` : ''}
                </Text>
              ))}
              {lastSnapshot.records.length > 4 && (
                <Text type="secondary">……共 {lastSnapshot.records.length} 条</Text>
              )}
            </Space>
          )}
          {(lastSnapshot.missingRecords ?? 0) > 0 || (lastSnapshot.missingFiles ?? 0) > 0 ? (
            <Alert
              type="warning"
              showIcon
              message="导出缺失提醒"
              description={
                <span>
                  缺少记录：{lastSnapshot.missingRecords ?? 0}，缺少主文件：{lastSnapshot.missingFiles ?? 0}，
                  缺少参考图：{lastSnapshot.missingReferences ?? 0}。
                  请查看 <Text code>docs/verification_logs/</Text> 目录中的导出日志。
                </span>
              }
            />
          ) : (
            <Text type="secondary">
              参考图数量：{lastSnapshot.referenceCount ?? 0}。日志位于 <Text code>docs/verification_logs/</Text>。
            </Text>
          )}
        </Space>
      )}
