{"output":"import { create } from 'zustand'
import { immer } from 'zustand/middleware/immer'

import type { EnvelopeSummaryPayload } from '@shared/ipc-types'

import type { ModelKey } from '../constants/schema'
import { getModelLabel } from '../constants/schema'
import { useHistoryStore } from './history.store'

export type TaskStatus = 'PENDING' | 'RUNNING' | 'SUCCEEDED' | 'FAILED' | 'CANCELLED'

export interface TaskRecord {
  id: string
  model: ModelKey
  modelLabel?: string
  status: TaskStatus
  createdAt: number
  updatedAt: number
  schemaVersion?: string
  input: Record<string, unknown>
  envelope?: EnvelopeSummaryPayload
  error?: string | null
  fileId?: string | null
  outputPath?: string | null
  thumbnailPath?: string | null
  thumbnailRelativePath?: string | null
  thumbnailStrategy?: string | null
  relativePath?: string | null
  exportResult?: ExportSnapshot | null
}

export interface ExportSnapshotRecord {
  fileId: string
  filename?: string | null
  recordType?: 'image' | 'video'
  referenceCount?: number | null
}

export interface ExportSnapshot {
  zipPath?: string | null
  destinationZip?: string | null
  manifestPath?: string | null
  referenceCount?: number | null
  missingRecords?: number | null
  missingFiles?: number | null
  missingReferences?: number | null
  executedAt: number
  records?: ExportSnapshotRecord[]
  status?: string | null
  opId?: string | null
  storageRoot?: string | null
}

export interface EnqueueTaskInput {
  id?: string
  model: ModelKey
  schemaVersion?: string
  input: Record<string, unknown>
  status?: TaskStatus
}

export interface EnvelopeUpdate {
  taskId: string
  envelope: EnvelopeSummaryPayload
  fallbackMessage?: string
}

interface TaskOrchestratorState {
  tasks: Record<string, TaskRecord>
  order: string[]
  enqueueTask: (input: EnqueueTaskInput) => string
  updateTaskFromEnvelope: (update: EnvelopeUpdate) => void
  updateTaskExportResult: (taskId: string, snapshot: ExportSnapshot) => void
  abortTask: (taskId: string, reason?: string) => void
  removeTask: (taskId: string) => void
  clearCompleted: () => void
  reset: () => void
}

const INITIAL_STATE: Pick<TaskOrchestratorState, 'tasks' | 'order'> = {
  tasks: {},
  order: []
}

const generateTaskId = (): string => {
  if (typeof crypto !== 'undefined' && typeof crypto.randomUUID === 'function') {
    return crypto.randomUUID()
  }
  const random = Math.random().toString(36).slice(2, 10)
  return `task_${Date.now().toString(36)}_${random}`
}

export const useTaskOrchestrator = create<TaskOrchestratorState>()(
  immer((set) => ({
    ...INITIAL_STATE,
    enqueueTask: (input) => {
      const now = Date.now()
      const taskId = input.id ?? generateTaskId()

      set((state) => {
        if (!state.tasks[taskId]) {
          state.order.unshift(taskId)
        }

        state.tasks[taskId] = {
          id: taskId,
          model: input.model,
          modelLabel: getModelLabel(input.model),
          schemaVersion: input.schemaVersion,
          input: input.input,
          status: input.status ?? 'PENDING',
          createdAt: state.tasks[taskId]?.createdAt ?? now,
          updatedAt: now,
          envelope: state.tasks[taskId]?.envelope,
          error: null,
          fileId: state.tasks[taskId]?.fileId ?? null,
          outputPath: state.tasks[taskId]?.outputPath ?? null,
          thumbnailPath: state.tasks[taskId]?.thumbnailPath ?? null,
          thumbnailRelativePath: state.tasks[taskId]?.thumbnailRelativePath ?? null,
          thumbnailStrategy: state.tasks[taskId]?.thumbnailStrategy ?? null,
          relativePath: state.tasks[taskId]?.relativePath ?? null,
          exportResult: state.tasks[taskId]?.exportResult ?? null
        }
      })

      return taskId
    },
    updateTaskFromEnvelope: ({ taskId, envelope, fallbackMessage }) => {
      let refreshType: 'image' | 'video' | null = null

      set((state) => {
        const record = state.tasks[taskId]
        if (!record) return

        const now = Date.now()
        record.envelope = envelope
        record.updatedAt = now

        if (envelope.status) {
          record.status = String(envelope.status).toUpperCase() as TaskStatus
        }

        const isSuccess = envelope.success ?? envelope.code === 'SUCCESS'
        record.error = isSuccess ? null : envelope.message ?? fallbackMessage ?? record.error ?? null

        const primaryOutput = (envelope.primary_output ?? {}) as Record<string, unknown>
        const storageLocal = (envelope.storage_local ?? {}) as { primary?: Record<string, unknown>; root?: string }
        const storagePrimary = storageLocal?.primary ?? {}
        const storageRoot = typeof storageLocal?.root === 'string' ? storageLocal.root : undefined

        const fileId = (primaryOutput.file_id ?? storagePrimary.file_id) as string | undefined
        if (fileId) {
          record.fileId = fileId
        }

        const localPath = (primaryOutput.local_path ?? storagePrimary.path) as string | undefined
        if (localPath) {
          record.outputPath = localPath
        }

