{"output":"import VirtualList from 'rc-virtual-list'
import { Badge, Spin, Typography } from 'antd'

import type { TaskRecord } from '../../store/taskOrchestrator'

const { Text } = Typography

const STATUS_BADGE: Record<TaskRecord['status'], 'default' | 'processing' | 'success' | 'error' | 'warning'> = {
  PENDING: 'default',
  RUNNING: 'processing',
  SUCCEEDED: 'success',
  FAILED: 'error',
  CANCELLED: 'warning'
}

interface TaskHistoryListProps {
  tasks: TaskRecord[]
  activeId: string | null
  height?: number
  onSelect: (taskId: string) => void
  onOpenDetail?: (taskId: string) => void
}

const DEFAULT_HEIGHT = 420
const ITEM_HEIGHT = 110

export const TaskHistoryList = ({
  tasks,
  activeId,
  height = DEFAULT_HEIGHT,
  onSelect,
  onOpenDetail
}: TaskHistoryListProps) => (
  <div className="task-history-list">
    <VirtualList
      data={tasks}
      height={height}
      itemHeight={ITEM_HEIGHT}
      itemKey="id"
      className="task-history-list__virtual"
    >
      {(task) => {
        const isActive = task.id === activeId
        const badgeStatus = STATUS_BADGE[task.status] ?? 'default'
        const isProcessing = task.status === 'PENDING' || task.status === 'RUNNING'

        const tailId = task.id.slice(-8)
        const updatedLabel = task.updatedAt
          ? new Date(task.updatedAt).toLocaleString()
          : task.createdAt
            ? new Date(task.createdAt).toLocaleString()
            : '—'

        return (
          <div
            key={task.id}
            className={`task-history-item${isActive ? ' task-history-item--active' : ''}`}
            onClick={() => onSelect(task.id)}
            onDoubleClick={() => onOpenDetail?.(task.id)}
          >
            <div className="task-history-item__header">
              <Text strong>{task.modelLabel ?? task.model}</Text>
              <Badge status={badgeStatus} text={task.status} />
              {isProcessing && <Spin size="small" style={{ marginLeft: 8 }} />}
            </div>
            <div className="task-history-item__meta">
              <Text type="secondary">#{tailId}</Text>
              <Text type="secondary">{updatedLabel}</Text>
            </div>
            {task.envelope?.message && (
              <Text type="secondary" className="task-history-item__message" ellipsis>
                {task.envelope.message}
              </Text>
            )}
          </div>
        )
      }}
    </VirtualList>
  </div>
)

export default TaskHistoryList
