interface ReferenceEntry {
  key: string
  items: string[]
}

const collectReferenceEntries = (task: TaskRecord | null): ReferenceEntry[] => {
  if (!task) return []

  const entries: ReferenceEntry[] = []

  const pushArray = (key: string, value: unknown) => {
    if (!Array.isArray(value)) return
    const normalized = value
      .map((item) => {
        if (!item) return ''
        if (typeof item === 'string') {
          return item
        }
        if (typeof item === 'object') {
          const record = item as Record<string, unknown>
          const direct = ['url', 'path', 'id', 'fileId', 'assetId', 'name'].reduce<string | null>(
            (prev, field) => {
              if (prev) return prev
              const candidate = record[field]
              if (typeof candidate === 'string' && candidate) {
                return candidate
              }
              return null
            },
            null
          )
          if (direct) {
            return direct
          }
          try {
            return JSON.stringify(record)
          } catch {
            return '[object]'
          }
        }
        return String(item)
      })
      .map((item) => item.trim())
      .filter((item) => item.length > 0)

    if (normalized.length === 0) return

    const existing = entries.find((entry) => entry.key === key)
    if (existing) {
      existing.items.push(...normalized)
    } else {
      entries.push({ key, items: normalized })
    }
  }

  const inputEntries = task.input ?? {}
  Object.entries(inputEntries).forEach(([key, value]) => {
    if (/reference/i.test(key)) {
      pushArray(`input.${key}`, value)
    }
  })

  const metaEntries = task.envelope?.meta ?? null
  if (metaEntries && typeof metaEntries === 'object') {
    Object.entries(metaEntries).forEach(([key, value]) => {
      if (/reference/i.test(key)) {
        pushArray(`meta.${key}`, value)
      }
    })
  }

  const dataEntries = task.envelope?.data ?? null
  if (dataEntries && typeof dataEntries === 'object') {
    Object.entries(dataEntries).forEach(([key, value]) => {
      if (/reference/i.test(key)) {
        pushArray(`data.${key}`, value)
      }
    })
  }

  return entries
}

