const deepFreeze = <T extends Record<string, unknown>>(input: T): T => {
  Object.freeze(input)
  for (const value of Object.values(input)) {
    if (value && typeof value === 'object' && !Object.isFrozen(value)) {
      deepFreeze(value as Record<string, unknown>)
    }
  }
  return input
}

const api = deepFreeze({
  diagnostics: {
    ping: () => ipcRenderer.invoke('app:ping') as Promise<IpcResponse<string>>
  },
