import { fileURLToPath } from 'node:url'
import { registerIpcHandlers } from './ipc'

const __filename = fileURLToPath(import.meta.url)
const __dirname = dirname(__filename)

const resolvePreloadBundle = () => {
  const candidates = [
    join(__dirname, 'preload', 'index.mjs'),
    join(__dirname, 'preload.mjs'),
    join(__dirname, 'preload.cjs')
  ]

  for (const candidate of candidates) {
    if (existsSync(candidate)) {
      if (candidate.endsWith('.mjs')) {
        const cjsFallback = candidate.replace(/\.mjs$/, '.cjs')
        try {
          copyFileSync(candidate, cjsFallback)
          return cjsFallback
        } catch (error) {
          console.warn('[bootstrap] 无法创建预加载 CJS 备用文件', error)
          return candidate
        }
      }
      return candidate
    }
  }

  throw new Error(`Unable to locate preload bundle under ${__dirname}`)
}

const PRELOAD_BUNDLE = resolvePreloadBundle()

const appRoot = join(__dirname, '..')
process.env.APP_ROOT = appRoot
const APP_ROOT = process.env.APP_ROOT
const RENDERER_DIST = join(APP_ROOT, 'dist')

const injectBundledBinaries = () => {
  if (!app.isPackaged) {
    return
  }

  const resourcesDir = process.resourcesPath
  const binDir = join(resourcesDir, 'bin')

  if (!existsSync(binDir)) {
    console.warn(`[bootstrap] 未找到打包资源目录: ${binDir}`)
    return
  }

  const currentPath = process.env.PATH ?? ''
  const segments = currentPath.split(delimiter).filter(Boolean)
  if (!segments.includes(binDir)) {
    segments.unshift(binDir)
    process.env.PATH = segments.join(delimiter)
  }

  const ffmpegExecutable = process.platform === 'win32' ? 'ffmpeg.exe' : 'ffmpeg'
  const ffmpegPath = join(binDir, ffmpegExecutable)

  if (!process.env.FFMPEG_PATH) {
    process.env.FFMPEG_PATH = ffmpegPath
  }

  process.env.GEMENG_FFMPEG_BIN = ffmpegPath
}

process.env.VITE_PUBLIC = app.isPackaged
  ? RENDERER_DIST
  : join(process.cwd(), 'public')

let mainWindow: BrowserWindow | null = null

const createMainWindow = () => {
  const isDev = !app.isPackaged

  mainWindow = new BrowserWindow({
    width: 1280,
    height: 800,
    show: false,
    autoHideMenuBar: true,
    webPreferences: {
      contextIsolation: true,
      nodeIntegration: false,
      sandbox: false,
      webSecurity: isDev ? false : true,
      preload: PRELOAD_BUNDLE
    }
  })

  mainWindow.once('ready-to-show', () => {
    mainWindow?.show()
  })

  if (app.isPackaged) {
    mainWindow.loadFile(join(RENDERER_DIST, 'index.html'))
    return
  }

  const devServerUrl =
    process.env.VITE_DEV_SERVER_URL ??
    process.env.ELECTRON_RENDERER_URL ??
    'http://127.0.0.1:5173'

  if (!app.isPackaged) {
    mainWindow.loadURL(devServerUrl)
    return
  }

  mainWindow.loadFile(join(RENDERER_DIST, 'index.html'))
}

app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit()
  }
})

app.whenReady().then(() => {
  injectBundledBinaries()
  registerIpcHandlers()

  if (BrowserWindow.getAllWindows().length === 0) {
    createMainWindow()
  }

  app.on('activate', () => {
    if (BrowserWindow.getAllWindows().length === 0) {
      createMainWindow()
    }
  })
})
