{"output":""""Helpers to locate edition-specific assets (fonts, ffmpeg, etc.)."""

from __future__ import annotations

import os
from pathlib import Path
from typing import Optional
import tempfile
import shutil
import hashlib


def _is_ascii(path: Path) -> bool:
    try:
        str(path).encode("ascii")
        return True
    except UnicodeEncodeError:
        return False


def ensure_ascii_font_path(font_path: Path) -> Path:
    """Return a font path safe for ffmpeg (ASCII-only). Copies when needed."""

    if _is_ascii(font_path):
        return font_path

    cache_dir = Path(tempfile.gettempdir()) / "godream_trial_fonts"
    cache_dir.mkdir(parents=True, exist_ok=True)

    digest = hashlib.sha256(str(font_path).encode("utf-8")).hexdigest()[:16]
    cached_path = cache_dir / f"font_{digest}{font_path.suffix or '.otf'}"

    try:
        src_mtime = font_path.stat().st_mtime
        needs_copy = True
        if cached_path.exists():
            needs_copy = cached_path.stat().st_mtime < src_mtime
        if needs_copy:
            shutil.copy2(font_path, cached_path)
    except FileNotFoundError:
        return font_path
    except Exception:
        return font_path

    return cached_path


def _project_root() -> Path:
    return Path(__file__).resolve().parent.parent


def resources_dir() -> Path:
    env_dir = os.environ.get("GEMENG_TRIAL_RESOURCES")
    if env_dir:
        path = Path(env_dir).expanduser()
        if path.exists():
            return path
    candidate = Path(__file__).resolve().parent / "resources"
    if candidate.exists():
        return candidate
    root_candidate = _project_root() / "resources"
    return root_candidate


def resolve_font_path() -> Optional[Path]:
    env_font = os.environ.get("GEMENG_TRIAL_WATERMARK_FONT")
    if env_font:
        font_path = Path(env_font).expanduser()
        if font_path.exists():
            return font_path

    fonts_dir = resources_dir() / "fonts"
    for name in (
        "SourceHanSansCN-Regular.otf",
        "SourceHanSans-Regular.otf",
        "NotoSansCJK-Regular.ttc",
        "NotoSansSC-Regular.otf",
    ):
        candidate = fonts_dir / name
        if candidate.exists():
            return candidate

    system_candidates = [
        "/System/Library/Fonts/PingFang.ttc",  # macOS
        "/System/Library/Fonts/SFNS.ttf",
        "/System/Library/Fonts/Supplemental/SourceHanSansCN-Regular.otf",
        "/Library/Fonts/PingFang.ttc",
        "/Library/Fonts/Arial Unicode.ttf",
        "C:/Windows/Fonts/msyh.ttc",  # Windows å¾®è½¯é›…é»‘
        "C:/Windows/Fonts/msyhbd.ttc",
        "C:/Windows/Fonts/simsun.ttc",
        "/usr/share/fonts/opentype/noto/NotoSansCJK-Regular.ttc",  # Linux
        "/usr/share/fonts/opentype/noto/NotoSansSC-Regular.otf",
    ]

    for path in system_candidates:
        candidate = Path(path)
        if candidate.exists():
            return candidate

    return None


def resolve_ffmpeg_binary() -> Optional[Path]:
    env_bin = os.environ.get("FFMPEG_BINARY")
    if env_bin:
        candidate = Path(env_bin).expanduser()
        if candidate.exists():
            return candidate

    bin_dir = resources_dir() / "bin"
    for name in ("ffmpeg", "ffmpeg.exe"):
        candidate = bin_dir / name
        if candidate.exists():
            return candidate

    return None
