    @staticmethod
    def _format_remaining_time(seconds: Optional[int]) -> str:
        if seconds is None:
            return "æœªçŸ¥"
        if seconds <= 0:
            return "0 å°æ—¶"
        days = seconds // 86400
        hours = (seconds % 86400) // 3600
        minutes = (seconds % 3600) // 60
        parts = []
        if days:
            parts.append(f"{days} å¤©")
        if hours:
            parts.append(f"{hours} å°æ—¶")
        if minutes or not parts:
            parts.append(f"{max(minutes, 1)} åˆ†é’Ÿ")
        return " ".join(parts)

    def _count_files_in_dir(self, dir_path: Path) -> int:
        """ç»Ÿè®¡ç›®å½•ä¸­çš„æ–‡ä»¶æ•°é‡"""
        try:
            if not dir_path.exists():
                return 0
            return len([f for f in dir_path.rglob('*') if f.is_file() and f.suffix.lower() in ['.jpg', '.jpeg', '.png', '.gif', '.bmp', '.mp4', '.avi', '.mov', '.mkv']])
        except:
            return 0

    def _check_v2_csv_rw(self, root_path: str) -> tuple:
        """è½»é‡æ£€æµ‹ V2 CSV å¯è¯»/å†™ï¼›å¿…è¦æ—¶é€šè¿‡ .permcheck.tmp éªŒè¯å†™å…¥èƒ½åŠ›"""
        try:
            from modules.storage.storage_manager_factory import get_v2_storage_manager
            # ç¡®ä¿V2 CSVå·²åˆå§‹åŒ–
            _ = get_v2_storage_manager(root_path)
            csv_files = [
                Path(root_path) / 'images' / 'image_index_v2.csv',
                Path(root_path) / 'videos' / 'video_index_v2.csv',
                Path(root_path) / 'chats' / 'chat_history_v2.csv',
            ]
            read_ok = True
            write_ok = True
            for csvf in csv_files:
                # è¯»
                try:
                    with open(csvf, 'r', encoding='utf-8') as _f:
                        _ = _f.readline()
                except Exception:
                    read_ok = False
                # å†™ï¼ˆæƒé™ä¸å®é™…åˆ›å»ºä¸´æ—¶æ–‡ä»¶éªŒè¯ï¼‰
                parent = csvf.parent
                can_write = False
                try:
                    if csvf.exists():
                        can_write = os.access(csvf, os.W_OK) or os.access(parent, os.W_OK)
                    else:
                        can_write = os.access(parent, os.W_OK)
                    if not can_write:
                        tmp = parent / '.permcheck.tmp'
                        with open(tmp, 'w', encoding='utf-8') as tf:
                            tf.write('ok')
                        tmp.unlink(missing_ok=True)
                        can_write = True
                except Exception:
                    can_write = False
                if not can_write:
                    write_ok = False
            return (read_ok, write_ok)
