def ensure_api_ready(parent: Optional[QWidget], allow_dialog: bool = True) -> bool:
    _inject_api_key_env()
    issues = _gather_setup_issues()
    if issues:
        if allow_dialog:
            target_parent = parent if parent is not None else QApplication.activeWindow()
            QMessageBox.warning(
                target_parent,
                "配置提醒",
                "请完成以下设置后再尝试生成：\n" + "\n".join(f"• {item}" for item in issues),
            )
        return False
    return True


def _mark_license_invalid_if_needed(message: str) -> bool:
    if not message:
        return False
    normalized = message.lower()
    lower_triggers = ("401", "403", "unauthorized", "invalid token", "invalid key", "authentication failed")
    raw_triggers = ("密钥", "鉴权", "认证", "授权失败")
    if any(keyword in normalized for keyword in lower_triggers) or any(keyword in message for keyword in raw_triggers):
        try:
            config_manager.update_api_key("volcengine", "")
        except Exception:
            pass
        os.environ.pop("VOLCENGINE_API_KEY", None)
        return True
    return False


