def _parse_parameters_field(rec: Dict[str, Any]) -> Dict[str, Any]:
    params = rec.get('parameters', {})
    if isinstance(params, str):
        try:
            return json.loads(params) or {}
        except Exception:
            return {}
    return params or {}

def build_resolution_text(rec: Dict[str, Any]) -> str:
    """ä¼˜å…ˆæ˜¾ç¤º 'åˆ†è¾¨ç‡æ¡£ä½ + æ¯”ä¾‹'ï¼Œå¦åˆ™å›é€€ä¸º 'widthxheight'ï¼Œå†å¦åˆ™å›é€€åŸresolution/æœªçŸ¥"""
    try:
        params = _parse_parameters_field(rec)
        rg = params.get('resolution_grade') or rec.get('resolution_grade')
        rt = params.get('ratio') or rec.get('ratio')
        if rg and rt:
            return f"{str(rg)} {str(rt)}"
        if rg:
            return str(rg)
        # å›é€€ widthÃ—height
        width = rec.get('width') or params.get('width')
        height = rec.get('height') or params.get('height')
        # æœ‰äº›è®°å½• width/height å¯èƒ½æ˜¯å­—ç¬¦ä¸²æˆ–æµ®ç‚¹
        def _to_int(v):
            try:
                return int(float(v))
            except Exception:
                return None
        w = _to_int(width)
        h = _to_int(height)
        if w and h:
            return f"{w}x{h}"
        # æœ€åå›é€€å·²æœ‰çš„ resolution æ–‡æœ¬ï¼ˆå¦‚æœæœ‰ï¼‰
        res = rec.get('resolution')
        if isinstance(res, str) and res.strip():
            return res
        return 'æœªçŸ¥'
    except Exception:
        return 'æœªçŸ¥'

def pick_model_code(rec: Dict[str, Any]) -> str:
    """ä¼˜å…ˆé¡¶å±‚ modelï¼›å¦åˆ™ä» parameters.model/model_id/model_type/mode æ¨æ–­"""
    try:
        top = rec.get('model')
        if top:
            return str(top)
        params = _parse_parameters_field(rec)
        cand = params.get('model') or params.get('model_id')
        if cand:
            return str(cand)
        # model_type: 'pro' | 'lite'
        mt = params.get('model_type') or rec.get('model_type')
        if isinstance(mt, str) and mt.strip():
            return mt.strip()
        # ä» mode æ–‡æœ¬ä¸­ç²—ç•¥è¯†åˆ«ï¼ˆä¾‹å¦‚â€œæ–‡ç”Ÿè§†é¢‘(Pro)â€ï¼‰
        mode_txt = params.get('mode') or rec.get('mode') or ''
        s = str(mode_txt).lower()
        if 'pro' in s:
            return 'pro'
        if 'lite' in s or 'i2v' in s or 't2v' in s:
            return 'lite'
        # å…œåº•
        return 'seedance'
    except Exception:
        return 'seedance'


def _evaluate_dependency_rules(rules: Sequence[Dict[str, Any]], values: Dict[str, Any]) -> bool:
    if not rules:
        return True

    def _truthy(val: Any) -> bool:
        return bool(val) and val not in ('', None)

    for rule in rules:
        field = rule.get('field')
        if not field:
            continue
        current = values.get(field)
        if rule.get('exists') and not _truthy(current):
            return False
        if 'equals' in rule and current != rule.get('equals'):
            return False
        if 'not_equals' in rule and current == rule.get('not_equals'):
            return False
    return True


def _mask_reference_ids(assets: Sequence[Dict[str, Any]]) -> str:
    masked: List[str] = []
    for rec in assets or []:
        rid = rec.get('id') if isinstance(rec, dict) else None
        if rid:
            masked.append(f"{str(rid)[:6]}***")
    return ",".join(masked) if masked else "-"


def _mask_record_id(record_id: Any) -> str:
    if not record_id:
        return "-"
    text = str(record_id)
    return f"{text[:6]}***"


def _summarize_masked_ids(ids: Sequence[str], limit: int = 5) -> str:
    masked = [f"{str(rid)[:6]}***" for rid in ids if rid]
    if not masked:
        return "-"
    if len(masked) > limit:
        return ",".join(masked[:limit]) + ",..."
    return ",".join(masked)


def _format_dir_hint(path: Path) -> str:
    try:
        directory = Path(path)
        name = directory.name
    except Exception:
        return "â€¦"
    return f"â€¦/{name}" if name else "â€¦"
