    def _calculate_grid(self, available_width: int, spacing: int) -> Tuple[int, int]:
        available_width = max(1, available_width)
        max_columns = max(1, min(6, len(self._thumb_items) or 1))
        preferred = self._card_default_width

        best_columns = 1
        best_width = min(self._card_max_width, max(self._card_min_width, available_width))
        best_score = abs(best_width - preferred)

        for cols in range(1, max_columns + 1):
            total_spacing = spacing * (cols - 1)
            width_per_col = (available_width - total_spacing) / cols
            if width_per_col < self._card_min_width:
                break
            card_width = min(self._card_max_width, width_per_col)
            score = abs(card_width - preferred)
            if score < best_score - 1e-6 or (abs(score - best_score) <= 1e-6 and cols > best_columns):
