                best_width = card_width
                best_score = score

        return max(1, best_columns), int(max(self._card_min_width, min(self._card_max_width, best_width)))

    def _compute_caption_font_size(self, card_width: int) -> int:
        base = 11
        if card_width <= self._card_min_width:
            return base
        extra = int((card_width - self._card_min_width) / 60)
        return min(14, max(11, base + extra))
