{"function":"() => {
  const sleep = (ms) => new Promise(resolve => setTimeout(resolve, ms));
  const scenarioTabs = Array.from(document.querySelectorAll('.arco-tabs-header-title'));
  const results = [];
  function getLanguages(select) {
    const popupId = select.getAttribute('aria-controls');
    select.click();
    const popup = document.getElementById(popupId);
    const langs = popup ? Array.from(popup.querySelectorAll('li')).map(li => li.innerText.trim()).filter(Boolean) : [];
    select.click();
    return {langs, popupId};
  }
  for (const tab of scenarioTabs) {
    tab.click();
    const scenarioTitle = tab.innerText.trim();
    const pane = document.querySelector('.arco-tabs-content-item-active');
    if (!pane) continue;
    const cards = Array.from(pane.querySelectorAll('.volc-doceditor-sample-card'));
    const cardInfos = [];
    for (const card of cards) {
      const titleEl = card.querySelector('.volc-doceditor-sample-title p');
      const cardTitle = titleEl ? titleEl.innerText.trim() : '';
      const select = card.querySelector('.arco-select');
      const codeBlock = card.querySelector('pre');
      const snippets = [];
      if (select && codeBlock) {
        select.scrollIntoView();
        const {langs, popupId} = getLanguages(select);
        for (const lang of langs) {
          select.click();
          const popup = document.getElementById(popupId);
          if (!popup) continue;
          const option = Array.from(popup.querySelectorAll('li')).find(li => li.innerText.trim() === lang);
          if (!option) continue;
          option.click();
          snippets.push({language: lang, code: codeBlock.innerText});
        }
      } else if (codeBlock) {
        snippets.push({language: 'default', code: codeBlock.innerText});
      }
      cardInfos.push({title: cardTitle, snippets});
    }
    results.push({scenario: scenarioTitle, cards: cardInfos});
  }
  return results;
}"}

