{"function":"() => {
  const walker = document.createTreeWalker(document.body, NodeFilter.SHOW_TEXT);
  let targetEl = null;
  while (walker.nextNode()) {
    const t = walker.currentNode.textContent.trim();
    if (t.includes('创建视频生成任务 API')) {
      targetEl = walker.currentNode.parentElement;
      break;
    }
  }
  if (!targetEl) return null;
  let el = targetEl;
  for (let i=0;i<5 && el.parentElement;i++) el = el.parentElement;
  return {tag: el.tagName, class: el.className, id: el.id, textSample: el.innerText.slice(0,500)};
}"}

