"""
Theme Engine - Enterprise Design System Core
负责管理应用主题、颜色Token解析、系统字体适配及图标着色。
"""

import json
import os
import platform
import re
from typing import Dict, Any, Optional
from PySide6.QtGui import QColor, QIcon, QPixmap, QPainter
from PySide6.QtCore import Qt, QObject, Signal
from utils.logger import logger

class ThemeEngine(QObject):
    """
    主题引擎单例

    职责:
    1. 加载 JSON 主题配置
    2. 提供语义化颜色 Token (e.g. 'palette.brand.main')
    3. 解析系统字体 (System Font Stack)
    4. 提供图标动态着色能力 (Icon Tinting)
    """

    _instance = None
    themeLoaded = Signal()

    def __new__(cls):
        if cls._instance is None:
            cls._instance = super(ThemeEngine, cls).__new__(cls)
            cls._instance._initialized = False
        return cls._instance

    def __init__(self):
        super().__init__()
        if self._initialized:
            return

        self._current_theme: Dict[str, Any] = {}
        self._flattened_tokens: Dict[str, str] = {}
        self._font_family_map = {
            "System-UI": self._resolve_system_ui_font(),
            "System-Mono": self._resolve_system_mono_font()
        }
        self._initialized = True
        logger.info("ThemeEngine initialized")

    def _resolve_system_ui_font(self) -> str:
        """解析系统 UI 字体"""
        system = platform.system()
        if system == "Darwin":  # macOS
            return ".AppleSystemUIFont"
        elif system == "Windows":
            return "Microsoft YaHei UI"
        else:
            return "Sans Serif"

    def _resolve_system_mono_font(self) -> str:
        """解析系统等宽字体"""
        system = platform.system()
        if system == "Darwin":
            return "Menlo"
        elif system == "Windows":
            return "Consolas"
        else:
            return "Monospace"

    @property
    def is_dark_mode(self) -> bool:
        """判断当前是否为暗色主题"""
        return self._current_theme.get("type", "").lower() == "dark"

    def get_value(self, token_path: str, default: any = None) -> any:
        """
        获取任意配置值（不限于颜色）
        :param token_path: e.g. 'layout.radius.base' 或 'fonts.family_base'
        :return: 配置值，找不到则返回 default
        """
        return self._flattened_tokens.get(token_path, default)

    def toggle_theme(self) -> bool:
        """
        日夜模式切换
        注意：主题加载后会发射 themeLoaded 信号，StyleSystem 应监听此信号自动刷新
        """
        current_name = self._current_theme.get("name", "").lower()
        target_theme = "linear_light" if "dark" in current_name else "linear_dark"

        return self.load_theme(f"{target_theme}.json")

    def load_theme(self, theme_path: str) -> bool:
        """
        加载主题文件 (JSON)
        :param theme_path: 绝对路径或相对路径
        """
        try:
            # 如果是相对路径，尝试在 config/themes 下查找
            if not os.path.isabs(theme_path):
                base_dir = os.path.dirname(os.path.dirname(os.path.abspath(__file__)))
                potential_path = os.path.join(base_dir, "config", "themes", theme_path)
                if os.path.exists(potential_path):
                    theme_path = potential_path
                elif not theme_path.endswith(".json"):
                    potential_path_json = potential_path + ".json"
                    if os.path.exists(potential_path_json):
                        theme_path = potential_path_json

            logger.info(f"Loading theme from: {theme_path}")

            with open(theme_path, 'r', encoding='utf-8') as f:
                self._current_theme = json.load(f)

            self._flatten_tokens(self._current_theme)
            self.themeLoaded.emit()
            logger.info(f"Theme '{self._current_theme.get('name')}' loaded successfully")
            return True

        except Exception as e:
            logger.error(f"Failed to load theme: {e}")
            return False

    def _flatten_tokens(self, data: Dict, prefix: str = ""):
        """将嵌套的 JSON 展平为 dot.notation 键值对"""
        for key, value in data.items():
            current_key = f"{prefix}.{key}" if prefix else key
            if isinstance(value, dict):
                self._flatten_tokens(value, current_key)
            else:
                # 保留原始类型（数字、字符串等），只对颜色值保持字符串
                self._flattened_tokens[current_key] = value

    def get_color(self, token_path: str, default: str = "magenta") -> str:
        """
        获取颜色 Token 值
        :param token_path: e.g. 'palette.brand.main'
        :return: Hex color string or rgba string
        """
        # 支持简写 (省略 palette.)
        if not token_path.startswith("palette.") and "palette." + token_path in self._flattened_tokens:
            token_path = "palette." + token_path

        value = self._flattened_tokens.get(token_path, default)

        # 解析 token 引用 (e.g. "{palette.neutral.bg_app}") - 简单实现
        if value.startswith("{") and value.endswith("}"):
            ref_key = value[1:-1]
            return self.get_color(ref_key, default)

        return value

    def get_qcolor(self, token_path: str, default: str = "magenta") -> QColor:
        """
        获取颜色 Token 并转换为 QColor（支持 hex / rgb() / rgba() / Qt named colors）。
        """
        return self._parse_qcolor(self.get_color(token_path, default=default), default=default)

    def _parse_qcolor(self, value: str, default: str = "magenta") -> QColor:
        raw = (value or "").strip()
        if not raw:
            return QColor(default)

        # Fast path: hex / named
        if raw.startswith("#"):
            return QColor(raw)

        # Parse css-like rgb()/rgba()
        m = re.match(r"^rgba?\((.+)\)$", raw.replace(" ", ""), flags=re.IGNORECASE)
        if m:
            parts = m.group(1).split(",")
            if len(parts) in (3, 4):
                try:
                    r = int(float(parts[0]))
                    g = int(float(parts[1]))
                    b = int(float(parts[2]))
                    if len(parts) == 4:
                        a_raw = float(parts[3])
                        a = int(round(a_raw * 255)) if 0.0 <= a_raw <= 1.0 else int(round(a_raw))
                    else:
                        a = 255
                    return QColor(r, g, b, max(0, min(255, a)))
                except Exception:
                    return QColor(default)

        c = QColor(raw)
        return c if c.isValid() else QColor(default)

    def get_font_family(self, token: str = "family_base") -> str:
        """获取解析后的字体族名称"""
        raw_font = self._current_theme.get("fonts", {}).get(token, "System-UI")
        return self._font_family_map.get(raw_font, raw_font)

