#!/usr/bin/env python3
# -*- coding: utf-8 -*-
"""
TaskRunner - 统一后台任务调度（I/O、CPU）

第一版实现基于 concurrent.futures.ThreadPoolExecutor：
- 提供 submit/cancel 能力
- 支持按 key 去重（同 key 的重复任务丢弃或复用）
- 可传入取消令牌；回调 on_done/on_error

后续可无缝替换为 QThreadPool/QtConcurrent + QFutureWatcher（仅需保持同名接口）。
"""

import threading
from concurrent.futures import ThreadPoolExecutor, Future
from typing import Callable, Optional, Dict, Any
import os


class CancelToken:
    def __init__(self):
        self._cancelled = False
        self._lock = threading.Lock()

    def cancel(self):
        with self._lock:
            self._cancelled = True

    @property
    def cancelled(self) -> bool:
        with self._lock:
            return self._cancelled


class TaskRunner:
    _instance = None
    _lock = threading.Lock()

    def __init__(self, max_workers: Optional[int] = None):
        if max_workers is None:
            # 经验值：I/O 密集型任务线程数可略大于 CPU 核数
            max_workers = min(32, (os.cpu_count() or 4) * 2)
        self._executor = ThreadPoolExecutor(max_workers=max_workers, thread_name_prefix='TaskRunner')
        self._futures_by_key: Dict[str, Future] = {}
        self._futures_lock = threading.Lock()

    @classmethod
    def instance(cls) -> 'TaskRunner':
        with cls._lock:
            if cls._instance is None:
                cls._instance = TaskRunner()
            return cls._instance

    def submit(
        self,
        task: Callable[[], Any],
        *,
        key: Optional[str] = None,
        cancel_token: Optional[CancelToken] = None,
        on_done: Optional[Callable[[Any], None]] = None,
        on_error: Optional[Callable[[BaseException], None]] = None,
    ) -> Future:
        """
        提交任务到后台执行

        - key: 用于去重（同 key 的未完成任务存在时，不再提交新的）
        - cancel_token: 由任务函数内部自查 token.cancelled 实现协作式取消
        - on_done/on_error: 回调在工作线程触发（避免直接更新 Qt UI），由调用方负责切回主线程
        """
        if key:
            with self._futures_lock:
                old = self._futures_by_key.get(key)
                if old and not old.done():
                    return old  # 去重：复用未完成的 Future

        def _wrap():
            try:
                if cancel_token and cancel_token.cancelled:
                    return None
                result = task()
                if on_done:
                    try:
                        on_done(result)
                    except Exception:
                        pass
                return result
            except BaseException as e:
                if on_error:
                    try:
                        on_error(e)
                    except Exception:
                        pass
                raise

        fut = self._executor.submit(_wrap)

        if key:
            with self._futures_lock:
                self._futures_by_key[key] = fut
        return fut

    def cancel_by_key(self, key_prefix: str):
        """尝试取消所有匹配 key 前缀的任务（协作式，需任务检查 CancelToken）"""
        with self._futures_lock:
            to_cancel = [k for k in self._futures_by_key.keys() if k.startswith(key_prefix)]
            for k in to_cancel:
                fut = self._futures_by_key.get(k)
                if fut and not fut.done():
                    fut.cancel()

    def shutdown(self, wait: bool = False):
        self._executor.shutdown(wait=wait)


