                items = result.get('items', []) if isinstance(result, dict) else (result or [])
                items = self._normalize_pending_records(items)
                self._next_cursor = result.get('next_cursor') if isinstance(result, dict) else None
                if items:
                    self.items_appended.emit(items)
                    self.update_status.emit(f"累计加载 {self.model.rowCount() + len(items)} 张图片")
            finally:
                self._loading = False

        def _on_error(exc: BaseException):
            try:
                self.update_status.emit(f"加载更多图片失败: {exc}")
            finally:
                self._loading = False

        task_runner.submit(_task, key=f"image-history-view:next:{self._next_cursor}", on_done=_on_done, on_error=_on_error)

    def refresh_record(self, record_id: str) -> bool:
        """刷新指定记录（从数据库重新加载）。返回是否成功。"""
        if not self._repo or not record_id:
            return False

